const ViewCard = () => {
    return (
        <div className='ViewCardContainer'>
            <div className='ViewCardInteract'>
                <img src={thumbnail} alt='test image'></img>
                <div className='ViewCardButton'>
                    <Button>READ</Button>
                    <div className='ViewCardButtonInteract'>
                        <Button><i class="fa-regular fa-heart"></i>Like</Button>
                        <Button><i class="fa-regular fa-share-from-square"></i>Share</Button>
                    </div>
                </div>
            </div>

            <div className='ViewCardInfoContainer'>
                <div className='ViewCardInfoList'>
                    <p className='title'>Title</p>
                    <div className='author'>
                        <img src={avatar} alt='avatar'></img>
                        <p>Author</p>
                    </div>
                    <div className='GenreList'>
                        <p className='genre'>Genre 1</p>
                        <p className='genre'>Genre 2</p>
                        <p className='genre'>Genre 3</p>
                    </div>
                    <p className='script'>Lorem ipsum dolor sit amet consectetur. Quis eu tincidunt commodo a congue facilisis risus odio. Ut sagittis egestas et pellentesque mi quam lectus vel. Nibh vulputate eros pretium tincidunt nulla non ultrices euismod. Leo gravida suspendisse egestas bibendum. Euismod lacus dignissim viverra arcu magnis tortor eget porttitor. Iaculis nullam ut commodo egestas pellentesque. Facilisi pellentesque eu libero non eu lacus scelerisque diam. Cursus blandit mauris morbi vitae auctor. Porttitor natoque vitae at fames. Turpis feugiat diam auctor nec. Dui sit orci tincidunt elit id aenean.</p>
                </div>
                <div className='NovelReact'>
                    <div className='react'><i class="fa-solid fa-heart"></i>1000</div>
                    <div className='react'><i class="fa-solid fa-share"></i>1000</div>
                </div>
            </div>

            <div className='ViewCardChaperList'>
                <p>Chapters</p>
                <ul></ul>
            </div>
        </div>

    );
};

export default ViewCard
